USE DB_PROJECT;

CREATE TABLE vendor (
	v_id INT IDENTITY NOT NULL,  
	v_name VARCHAR(128) NOT NULL,
	PRIMARY KEY (v_id)
);

CREATE TABLE product_brand (
	b_id int IDENTITY NOT NULL, 
	b_name VARCHAR(128) NOT NULL,
	PRIMARY KEY (b_id)
);

CREATE TABLE product_category (
	pc_id INT IDENTITY NOT NULL,
	pc_name VARCHAR(128) NOT NULL,
	PRIMARY KEY (pc_id)
);

CREATE TABLE product(
	p_id int IDENTITY NOT NULL,
	brand_id int NOT NULL,
	category_id int NOT NULL,
	price int NOT NULL,
	quantity int NOT NULL DEFAULT 0,
	PRIMARY KEY (p_id),
	FOREIGN KEY (brand_id) REFERENCES product_brand(b_id),
	FOREIGN KEY (category_id) REFERENCES product_category(pc_id)
);

CREATE TABLE employee(
	e_id INT IDENTITY NOT NULL,
	e_name VARCHAR(255) NOT NULL,
	email VARCHAR(255) NULL,
	cnic VARCHAR(15) UNIQUE,
	addres TEXT,
	mobile VARCHAR(255) UNIQUE,
	join_date DATETIME NOT NULL DEFAULT GETDATE(),
	base_salary INT NOT NULL,
	PRIMARY KEY (e_id)
);

CREATE TABLE salary_list(
	sl_id INT IDENTITY NOT NULL,	
	date_time DATETIME NOT NULL DEFAULT GETDATE(),
	salary INT NOT NULL,
	employee_id INT NOT NULL,
	discount INT NOT NULL DEFAULT 0,
	PRIMARY KEY (sl_id),
	FOREIGN KEY (employee_id) REFERENCES employee(e_id)
);

CREATE TABLE sale(
	s_id INT IDENTITY NOT NULL, 
	employee_id INT NOT NULL,
	date_time DATETIME NOT NULL DEFAULT GETDATE(),
	PRIMARY KEY (s_id),
	FOREIGN KEY (employee_id) REFERENCES employee(e_id)
);

CREATE TABLE sale_item(
	si_id INT IDENTITY NOT NULL,
	product_id INT NOT NULL,
	sale_id INT NOT NULL,
	quantity INT NOT NULL,
	PRIMARY KEY (si_id),
	FOREIGN KEY (product_id) REFERENCES product(p_id),
	FOREIGN KEY (sale_id) REFERENCES sale(s_id) ON DELETE CASCADE
);

CREATE TABLE purchase(
	pu_id INT IDENTITY NOT NULL,
	vendor_id INT NOT NULL,
	date_time DATETIME NOT NULL DEFAULT GETDATE(),
	PRIMARY KEY (pu_id),
	FOREIGN KEY (vendor_id) REFERENCES vendor(v_id)
);

CREATE TABLE purchase_item(
	pi_id INT IDENTITY NOT NULL,
	product_id INT NOT NULL ,
	purchase_id INT NOT NULL ,
	quantity INT NOT NULL,
	price INT NOT NULL,
	PRIMARY KEY (pi_id),
	FOREIGN KEY (product_id) REFERENCES product(p_id),
	FOREIGN KEY (purchase_id) REFERENCES purchase(pu_id) ON DELETE CASCADE
);

